$(function(){"use strict";$(".sortable").nestedSortable({listType:"ul",tabSize:25,expandOnHover:700,startCollapsed:false,isTree:true,maxLevels:5,forcePlaceholderSize:true,handle:"div",items:"li",helper:"clone",opacity:.6,placeholder:"placeholder",revert:250,tolerance:"pointer"})});$(function(){"use strict";$(".save-menu").on("click",function(){var mlist=$(this).data("menu");var serialized=$("#"+mlist).nestedSortable("serialize");var request=$.ajax({url:"index.php?p=categories",type:"POST",data:serialized,dataType:"json",cache:false});request.done(function(data){if(data.status=="success"){$("#"+mlist+" li").animate({backgroundColor:"#C9FFC9"},100).animate({backgroundColor:"#F9F9F9"},1e3);$.notify({icon:"fa fa-check-square-o",message:data.html},{type:"success"})}else{$("#"+mlist+" li").animate({backgroundColor:"#FFC9C9"},100).animate({backgroundColor:"#F9F9F9"},1e3);$.notify({icon:"fa fa-exclamation-triangle",message:data.html},{type:"danger"})}})})});