$(function(){"use strict";$(".envo_hooks_move").sortable({placeholder:"ui-state-highlight",axis:"y",revert:250,tolerance:"pointer",containment:"document",forcePlaceholderSize:true,start:function(e,ui){ui.placeholder.height(ui.item.height());ui.item.css("opacity","0.6");ui.placeholder.css("background-color","#CFF5F2")},update:function(){var arr=$(".envo_hooks_move").sortable("toArray");arr=$.map(arr,function(val,key){return val.replace("hook-","")});var request=$.ajax({url:"ajax/hookorder.php",type:"POST",data:{id:1,positions:arr},dataType:"json",cache:false});request.done(function(data){console.log(data.status);if(data.status=="success"){$(".envohooks").animate({backgroundColor:"#C9FFC9"},100).animate({backgroundColor:"#F9F9F9"},1e3);$.notify({icon:"fa fa-check-square-o",message:data.status_msg},{type:"success"})}else{$(".envohooks").animate({backgroundColor:"#FFC9C9"},100).animate({backgroundColor:"#F9F9F9"},1e3);$.notify({icon:"fa fa-exclamation-triangle",message:data.status_msg},{type:"danger"})}})},stop:function(e,ui){ui.item.css({top:"0",left:"0"});ui.item.css("opacity","1")}})});