$(function(){$("#envo_delete_all").click(function(){var checkedStatus=this.checked;if(checkedStatus){$("#button_delete").prop("disabled",false)}else{$("#button_delete").attr("disabled",true)}$(".highlight").each(function(){$(this).prop("checked",checkedStatus)})});$(".highlight").change(function(){if(this.checked){$("#button_delete").prop("disabled",false)}else{if($(".highlight").filter(":checked").length<1){$("#envo_delete_all").prop("checked",false);$("#button_delete").attr("disabled",true)}}})});