var TabManager={initialize:function(parent){var tabs=parent;var tabsHeight=tabs.innerHeight();if(tabsHeight>44){while(tabsHeight>44){var children=tabs.children("li:not(:last-child)");var count=children.length;var item=$(children[count-1]);var newMenuItem='<a class="dropdown-item" href="'+item.children("a").attr("href")+'" data-toggle="tab">'+item.text()+"</a>";tabs.find(".collapsed-tabs").prepend(newMenuItem);item.remove();tabsHeight=tabs.innerHeight()}}else{var count=0;while(tabsHeight<44&&tabs.children("li").length>0&&count++<20){var collapsed=tabs.find(".collapsed-tabs").children("a");var count=collapsed.length;if(count){var item=$(collapsed[0]);var newMenuItem="<li class='nav-item'>\n<a href='"+item.attr("href")+"' data-toggle='tab'>"+item.text()+"</a>\n</li>";tabs.children("li.collapsed-menu").before(newMenuItem);item.remove();tabsHeight=tabs.innerHeight()}else{break}}if(tabsHeight>44){TabManager.initialize(parent)}}if(!tabs.find(".collapsed-tabs").children("a").length){tabs.find(".collapsed-menu").addClass("hidden");tabs.find(".collapsed-menu").hide()}else{tabs.find(".collapsed-menu").removeClass("hidden");tabs.find(".collapsed-menu").show()}}};$(function(){TabManager.initialize($(".nav-tabs-responsive"));$(window).resize(function(){TabManager.initialize($(".nav-tabs-responsive"))})});